{
   "gearbox": {
      "scope": "gears",
      "schema": "v1"
   },
   "authorities": [
      "gearbox.works"
   ],
   "stacks": [
      "gearbox.works/wordpress",
      "gearbox.works/lamp",
      "gearbox.works/lemp",
      "gearbox.works/elasticsearch"
   ],
   "roles": {
      "gearbox.works/elasticsearch/elasticsearch": {
         "name": "Elasticsearch",
         "label": "Elasticsearch",
         "examples": [
            "Elasticsearch"
         ]
      },
      "gearbox.works/lamp/apache": {
         "name": "LAMP Apache Web Server",
         "label": "Apache",
         "examples": [
            "Apache"
         ]
      },
      "gearbox.works/lamp/mysql": {
         "name": "LAMP MySQL database server",
         "label": "MySQL",
         "examples": [
            "MySQL"
         ]
      },
      "gearbox.works/lamp/php": {
         "name": "LAMP PHP ProcessVM",
         "label": "PHP",
         "examples": [
            "PHP"
         ]
      },
      "gearbox.works/lemp/nginx": {
         "name": "LEMP Nginx Web server",
         "label": "Nginx",
         "examples": [
            "Nginx"
         ]
      },
      "gearbox.works/lemp/mysql": {
         "name": "LEMP Database server; MySQL or MariaDB",
         "label": "DB server",
         "examples": [
            "MySQL",
            "MariaDB"
         ]
      },
      "gearbox.works/lemp/p-script": {
         "name": "LEMP server-side scripting process VM; PHP, Python or Perl",
         "label": "server-side scripting",
         "examples": [
            "PHP"
         ]
      },
      "gearbox.works/wordpress/webserver": {
         "name": "Web server for WordPress",
         "label": "Web server",
         "examples": [
            "Apache",
            "Nginx",
            "Caddy",
            "Lighttpd"
         ]
      },
      "gearbox.works/wordpress/dbserver": {
         "name": "Database server for WordPress",
         "label": "DB server",
         "examples": [
            "MySQL",
            "MariaDB",
            "Percona"
         ]
      },
      "gearbox.works/wordpress/processvm": {
         "name": "ProcessVM (PHP) for WordPress",
         "label": "PHP",
         "examples": [
            "PHP",
            "HHVM"
         ]
      },
      "gearbox.works/wordpress/cacheserver": {
         "name": "Cache server for WordPress",
         "label": "Cache server",
         "examples": [
            "Redis",
            "Memcached"
         ],
         "optional": true,
         "max": 1
      }
   },
   "services": {
      "gearbox.works/elasticsearch/elasticsearch": {
         "org": "gearboxworks",
         "default": "elasticsearch",
         "options": [
            "elasticsearch:6.6.2"
         ]
      },
      "gearbox.works/lamp/apache": {
         "org": "gearboxworks",
         "default": "apache",
         "options": [
            "apache:2.4"
         ]
      },
      "gearbox.works/lamp/mysql": {
         "org": "gearboxworks",
         "default": "mysql",
         "options": [
            "mysql:5.5",
            "mysql:5.6",
            "mysql:5.7",
            "mysql:8.0"
         ]
      },
      "gearbox.works/lamp/php": {
         "org": "gearboxworks",
         "default": "php",
         "options": [
            "php:5.2",
            "php:5.6",
            "php:7.0",
            "php:7.1",
            "php:7.2"
         ]
      },
      "gearbox.works/lemp/nginx": {
         "org": "gearboxworks",
         "default": "nginx",
         "options": [
            "nginx:1.12",
            "nginx:1.13",
            "nginx:1.14",
            "nginx:1.15"
         ]
      },
      "gearbox.works/lemp/mysql": {
         "org": "gearboxworks",
         "default": "mariadb",
         "options": [
            "mariadb:5.5",
            "mariadb:10.0",
            "mariadb:10.1",
            "mariadb:10.2",
            "mariadb:10.3",
            "mysql:5.5",
            "mysql:5.6",
            "mysql:5.7",
            "mysql:8.0"
         ]
      },
      "gearbox.works/lemp/p-script": {
         "org": "gearboxworks",
         "default": "php",
         "options": [
            "php:5.2",
            "php:5.6",
            "php:7.0",
            "php:7.1",
            "php:7.2"
         ]
      },
      "gearbox.works/wordpress/webserver": {
         "org": "gearboxworks",
         "default": "nginx",
         "options": [
            "apache:2.4",
            "nginx:1.12",
            "nginx:1.13",
            "nginx:1.14",
            "nginx:1.15"
         ]
      },
      "gearbox.works/wordpress/dbserver": {
         "org": "gearboxworks",
         "default": "mariadb",
         "options": [
            "mariadb:5.5",
            "mariadb:10.0",
            "mariadb:10.1",
            "mariadb:10.2",
            "mariadb:10.3",
            "mysql:5.5",
            "mysql:5.6",
            "mysql:5.7",
            "mysql:8.0"
         ]
      },
      "gearbox.works/wordpress/processvm": {
         "org": "gearboxworks",
         "default": "php",
         "options": [
            "php:5.2",
            "php:5.6",
            "php:7.0",
            "php:7.1",
            "php:7.2"
         ]
      },
      "gearbox.works/wordpress/cacheserver": {
         "org": "gearboxworks",
         "default": "",
         "options": [
            "memcached:1.5",
            "redis:3.2",
            "redis:4.0"
         ]
      }
   }
}
